import { Container, Navbar, Nav, Offcanvas } from 'react-bootstrap';
import { useState } from 'react';
import RifatLogo from '../media/Logo.png';

import '../styles/navbar.css';
import horLine from '../media/LineHorizontal.png';

function NavBar() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return (
    <div>
      <Navbar variant="dark" expand="lg">
        <Container fluid className="NavContainer">
          <Navbar.Brand>
            <a href="#home">
              <img src={RifatLogo} className="logo" />
            </a>
          </Navbar.Brand>

          <Nav className="navHeader justify-content-end flex-grow-1 pe-3 ">
            <Nav.Link href="#about">
              <p className="nav-menu">Ãœber mich</p>
            </Nav.Link>

            <Nav.Link href="#projects">
              <p className="nav-menu">Projekte</p>
            </Nav.Link>
            <Nav.Link href="#contact">
              <p className="nav-menu">Kontakt</p>
            </Nav.Link>
          </Nav>
        </Container>

        <div className="phoneMenu">
          <Offcanvas show={show} onHide={handleClose} responsive="sm">
            <Offcanvas.Header closeButton>
              <Offcanvas.Title>Responsive offcanvas</Offcanvas.Title>
            </Offcanvas.Header>
            <Offcanvas.Body>
              <p className="mb-0">
                This is content within an <code>.offcanvas-lg</code>.
              </p>
            </Offcanvas.Body>
          </Offcanvas>
        </div>
      </Navbar>
    </div>
  );
}

export default NavBar;

